<?php/* Secretaria * * Contém as funções usadas apenas pela secretaria.*/class Secretaria extends CI_Controller {    public $template = 'admin_template';    public $title = "Sistema Acamp's > Secretaria";    public $css = array('reset', 'admin');    public $js = array();    /* Secretaria - construtor     *     * No contrutor carregamos apenas a biblioteca Session porque ela será     * usada em todo o Controller.    */    function __construct() {        parent::__construct();        $this->load->library('session');        $this->load->library('firephp');    }        /* index     *     * Essa função nunca é chamada.    */    function index(){        // Autenticação        if(!$this->session->userdata('logado')){            // se NÃO está logado, vai para a tela de login            redirect('admin/login');        }else{            redirect('admin/buscar');        }    }        /*     * fotos     *     *      */        function _zipar_fotos($ids){        $path = $this->config->item('upload_path', 'upload');                $this->load->library('zip');		        $this->load->model('pessoa_model');		$urls = $this->pessoa_model->pegar_fotos($ids);				foreach ($urls as $url) {			$nome_da_foto = ereg_replace($this->config->item('fotos_url'), '', $url);			$this->zip->read_file($path.$nome_da_foto);		}				$nome_do_zip = 'fotos '.date('d-m H-i').'.zip';        $this->zip->archive(CACHE_PATH.'secretaria/'.$nome_do_zip);		//$this->zip->download('fotos '.date('d-m H-i').'.zip');				return $nome_do_zip;    }		function historico() {				$log = simplexml_load_file(CACHE_PATH.'secretaria/log.xml');				$view['log'] = array();		foreach($log->children() as $registro){			if($registro->tipo == 'p'){				$registro->tipo = 'Participante';			}elseif($registro->tipo == 's'){				$registro->tipo = 'Serviço';			}elseif($registro->tipo == 'cv'){				$registro->tipo = 'Comunidade de Vida';			}elseif($registro->tipo == 'amigos'){				$registro->tipo = "Amigos do Acamp's";			}			$view['log'] []= $registro;		}				$this->load->view('/secretaria/historico', $view);			}		        function etiqueta($cd_tipo, $folha=false) {                // Autenticação        if(!$this->session->userdata('logado')){ // se NÃO está logado            redirect('admin/login');            return;        }                //----------------------------------------------------------------------                /*if($folha){                        $pessoas = array();            if($cd_tipo == 'p'){                $this->load->model('pessoa_model');                //$pessoas = $this->pessoa_model->etiqueta_participante();                //if(count($pessoas)==0)                    //redirect('admin/secretaria');            }elseif($cd_tipo == 's'){                $this->load->model('pessoa_model');                //$pessoas = $this->pessoa_model->etiqueta_servico();                //if(count($pessoas)==0)                    //redirect('admin/secretaria');            }elseif($cd_tipo == 'cv'){                            }                        if($folha == 'a4'){                $this->_a4($pessoas);            }elseif($folha == 'carta'){                $this->_carta($pessoas);            }            return;        }*/                //----------------------------------------------------------------------                if($this->input->post('gerar')){            			$this->load->model('pessoa_model');			            if($cd_tipo == 'p'){                $ids = $this->input->post('imprimir');                $pessoas = $this->pessoa_model->etiqueta_participante($ids);                				//$params = new SimpleXMLElement('<params></params>');            }elseif($cd_tipo == 's'){                $ids = $this->input->post('imprimir');                $pessoas = $this->pessoa_model->etiqueta_servico($ids);                				//$params = new SimpleXMLElement('<params></params>');            }elseif($cd_tipo == 'cv'){                $ids = $this->input->post('imprimir');                $pessoas = $this->pessoa_model->etiqueta_cv($ids);                				//$params = new SimpleXMLElement('<params></params>');            }elseif($cd_tipo == 'amigos'){                $ids = $this->input->post('imprimir');                $pessoas = $this->pessoa_model->etiqueta_servico($ids);                                foreach($pessoas as $i => $pessoa){                    $pessoas[$i]['cd_familia'] = $this->input->post($pessoa['id_pessoa']);                    $pessoas[$i]['cd_tipo'] = 'amigos';                }                            }            			//$nome_zip = $this->_zipar_fotos($ids);            $nome_pdf = $this->_a4($pessoas);			$log = simplexml_load_file(CACHE_PATH.'secretaria/log.xml');			$registro = $log->addChild('registro');			$registro->addChild('data', date('d/m/Y H:i'));			$registro->addChild('tipo', $cd_tipo);			/*if($cd_tipo != 'amigos'){				$registro->addChild($params);			}*/			$registro->addChild('fotos', $nome_zip);			$registro->addChild('etiquetas', $nome_pdf);			$log->asXML(CACHE_PATH.'secretaria/log.xml');        }elseif($this->input->post('verificar')){                        if($cd_tipo == 'p'){                                $id_ini = $this->input->post('id_ini');				if(!$id_ini) $id_ini = 0;                $id_fim = $this->input->post('id_fim');				if(!$id_fim) $id_fim = 999999;                                $this->load->model('pessoa_model');                $var['pessoas'] = $this->pessoa_model->verifica_etiqueta_participante($id_ini, $id_fim);                if(count($var['pessoas'])==0){                    redirect('admin/etiqueta/p');                }								$var['id_ini'] = $id_ini;				$var['id_fim'] = $id_fim;                            }elseif($cd_tipo == 's'){                                $id_servico = $this->input->post('id_servico');                                $this->load->model('pessoa_model');                $var['pessoas'] = $this->pessoa_model->verifica_etiqueta_servico($id_servico);                if(count($var['pessoas'])==0){                    redirect('admin/etiqueta/s');                }								$var['id_servico'] = $id_servico;                            }elseif($cd_tipo == 'cv'){                                $id_setor = $this->input->post('id_setor');                                $this->load->model('pessoa_model');                $var['pessoas'] = $this->pessoa_model->verifica_etiqueta_cv($id_setor);                if(count($var['pessoas'])==0){                    redirect('admin/etiqueta/cv');                }								$var['id_setor'] = $id_setor;                            }                        $var['tipo'] = $cd_tipo;            $var['form'] = false;            $this->load->view('secretaria/etiquetas_view', $var);                    }else{                        if($cd_tipo == 'amigos'){                                $var['tipo'] = $cd_tipo;                                $this->load->model('pessoa_model');                $var['pessoas'] = $this->pessoa_model->verifica_etiqueta_amigos();                                //if(count($var['pessoas'])==0){                //    redirect('admin/etiqueta/cv');                //}                                $this->load->model('familia');                $this->load->view('secretaria/etiquetas_view', $var);                return;            }                        $this->load->model('servico');            $this->load->model('setor');            $var['tipo'] = $cd_tipo;            $var['form'] = true; // Mostrar o form de verificação das etiquetas            $this->load->view('secretaria/etiquetas_view', $var);                    }    }        /*     * function a4     */    function _a4($pessoas) {	        $this->load->library('fpdf',array('orientation'=>'P', 'unit'=>'mm', 'format'=>'A4'));                //Papel A4 210 x 297        $this->fpdf->SetMargins(5,8); // 4 no meio        $this->fpdf->SetAutoPageBreak(true,8);                $this->fpdf->SetFont('Arial', '', 9);        //$this->fpdf->AddPage();                $d = 0;		//for($i=0; $i<40; $i++){        foreach($pessoas as $pessoa){            //$pessoa = $pessoas[$i%3];			if($d%20 == 0){				$this->fpdf->AddPage();				$x = $this->fpdf->GetX()+102;				$y = $this->fpdf->GetY()-25.5;			}			if($d%2 == 0){				$y += 25.5;				$x -= 102;				//$x = $this->fpdf->GetX();				//$y = $this->fpdf->GetY();			}else{				$x += 102;				//$y -= 25.545454;			}			$this->fpdf->SetXY($x, $y);			//$this->fpdf->Cell(98,25.54,'',1);						$this->fpdf->SetXY($x+3, $y);			$this->fpdf->SetFont('', 'B', 13);			$this->fpdf->Write(12.3,utf8_decode($pessoa['nm_cracha']));						$this->fpdf->SetXY($x+3, $y+7);			$this->fpdf->SetFont('', '', 9);			$this->fpdf->Write(8.6,utf8_decode($pessoa['nm_pessoa']));						$this->fpdf->SetXY($x+3, $y+10.9);			$this->fpdf->SetFont('', 'B', 10);			if($pessoa['cd_tipo'] == 'amigos'){				$this->fpdf->Write(10.3, utf8_decode('Seminário'));			}elseif($pessoa['cd_tipo'] == 'p'){				$this->fpdf->Write(10.3,utf8_decode($pessoa['ds_seminario']));			}else{				$this->fpdf->Write(10.3,utf8_decode($pessoa['nm_servico']));			}						$this->fpdf->SetXY($x+3, $y+16.9);			$this->fpdf->SetFont('', '', 9);			if($pessoa['cd_tipo'] == 'v'){				$this->fpdf->Write(8.5, 'CV: '.utf8_decode($pessoa['nm_setor']));			}else{				$this->fpdf->Write(8.5,utf8_decode($pessoa['nm_cidade']).' / '.$pessoa['cd_estado']);			}			//$this->fpdf->SetXY($x+70, $y);			$this->fpdf->SetXY($x+59, $y); // Correção para etiqueta 2011.1			if($pessoa['cd_tipo'] == 'p' || $pessoa['cd_tipo'] == 'amigos'){				$this->fpdf->Cell(11,19,$pessoa['id_pessoa'].' / '.$pessoa['cd_familia'],0,0,'C');			}else{				$this->fpdf->Cell(11,19,$pessoa['id_pessoa'],0,0,'C');			}			//Código de Barras			if(!file_exists($this->config->item('barcode_path').$pessoa['id_pessoa'].'.png')){				$this->_barcode($pessoa['id_pessoa']);			}			//$this->fpdf->SetXY($x+66, $y+11.6);			$this->fpdf->SetXY($x+55, $y+11.6); // Correção para etiqueta 2011.1			$this->fpdf->Image($this->config->item('barcode_path').$pessoa['id_pessoa'].'.png');						//if($d!=0 && $d%21 == 0){			//        $this->fpdf->AddPage();			//}elseif($d%2 == 1){			//        $this->fpdf->Ln(6.8);			//}						$d++;        }		$nome = 'etiquetas '.date('d-m H-i').'.pdf';		// Salvando no servidor		$this->fpdf->Output(CACHE_PATH.'secretaria/'.$nome,'F');		// Mandando para download		$this->fpdf->Output($nome,'I');		return $nome;    }        function _carta($pessoas) {	//$this->firephp->log($pessoas);		        $this->load->library('fpdf',array('orientation'=>'P', 'unit'=>'mm', 'format'=>'Letter'));                //Papel Carta 216 x 279        $this->fpdf->SetMargins(4,13); // 5 no meio        $this->fpdf->SetAutoPageBreak(true,13);                $this->fpdf->SetFont('Arial', '', 9);        //$this->fpdf->AddPage();                $d = 0;		//for($i=0; $i<40; $i++){        foreach($pessoas as $pessoa){            //$pessoa = $pessoas[$i%3];			if($d%20 == 0){				$this->fpdf->AddPage();				$x = $this->fpdf->GetX()+106.5;				$y = $this->fpdf->GetY()-25.5;			}			if($d%2 == 0){				$y += 25.5;				$x -= 106.5;				//$x = $this->fpdf->GetX();				//$y = $this->fpdf->GetY();			}else{				$x += 106.5;				//$y -= 25.545454;			}			$this->fpdf->SetXY($x, $y);			//$this->fpdf->Cell(101.5,25.3,'',1);						$this->fpdf->SetXY($x+3, $y);			$this->fpdf->SetFont('', 'B', 13);			$this->fpdf->Write(12.3,utf8_decode($pessoa['nm_cracha']));						$this->fpdf->SetXY($x+3, $y+7);			$this->fpdf->SetFont('', '', 9);			$this->fpdf->Write(8.6,utf8_decode($pessoa['nm_pessoa']));						$this->fpdf->SetXY($x+3, $y+10.9);			$this->fpdf->SetFont('', 'B', 10);			if($pessoa['cd_tipo'] == 'p'){				$this->fpdf->Write(10.3,utf8_decode($pessoa['ds_seminario']));			}else{				$this->fpdf->Write(10.3,utf8_decode($pessoa['nm_servico']));            }                        $this->fpdf->SetXY($x+3, $y+16.9);            $this->fpdf->SetFont('', '', 9);            if($pessoa['cd_tipo'] == 'v'){                $this->fpdf->Write(8.5, 'CV: '.utf8_decode($pessoa['nm_missao']));            }else{                $this->fpdf->Write(8.5,utf8_decode($pessoa['nm_cidade']).' / '.$pessoa['cd_estado']);			}            $this->fpdf->SetXY($x+70, $y);			if($pessoa['cd_tipo'] == 'p'){				$this->fpdf->Cell(11,19,$pessoa['id_pessoa'].' / '.$pessoa['cd_familia'],0,0,'C');            }else{				$this->fpdf->Cell(11,19,$pessoa['id_pessoa'],0,0,'C');			}            //Código de Barras            if(!file_exists($this->config->item('barcode_path').$pessoa['id_pessoa'].'.png')){                $this->_barcode($pessoa['id_pessoa']);            }            $this->fpdf->SetXY($x+66, $y+11.6);            $this->fpdf->Image($this->config->item('barcode_path').$pessoa['id_pessoa'].'.png');                        //if($d!=0 && $d%21 == 0){            //    $this->fpdf->AddPage();			//}elseif($d%2 == 1){            //    $this->fpdf->Ln(6.8);            //}                            $d++;        }        $this->fpdf->Output('teste.pdf','I');    }	    /*     * function barcode     * @param $id_pessoa     */        function _barcode($id_pessoa=0) {        $this->load->helper('barcode');        $bar = encode_128C($id_pessoa);		barcode_image($this->config->item('barcode_path').$id_pessoa.'.png', $bar,1,20);    }}